import { useState, useEffect } from "react"
import axiosRequest from "../../config/axiosConfig"
import { useDispatch } from "react-redux"
import { useNavigate } from "react-router-dom"

const AllMovies = () => {

    const[offSet, setOffSet] = useState(0)
    const[limit, setLimit] = useState(10)
    const[movies, setMovies] = useState([])
    const[message, setMessage] = useState('')
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const subUrl  = process.env.REACT_APP_SUBWS

    const getMovies = async () => {
        try {
            const {data} = await axiosRequest.get(`${subUrl}/movies?offset=${offSet}&limit=${limit}`)
            setMovies(data.movies)
        } catch (error) {
            if (error.response.status === 401) {
                setMessage(`${error.response.data.message}, you will be logged out in 3 seconds`)
                setTimeout(() => {
                    dispatch({type: 'RESET_USERS'})
                    dispatch({type: 'LOGOUT'})
                    localStorage.removeItem('token')
                    navigate('/')
                }, 3000)
            } else {
                setMessage(error.response.data.message)
                console.log(error)
            }
        }
    }

    useEffect(() => {
        getMovies()
    }, [])

    return (
        <>
        <h2>All Movies</h2>
        {
            movies.length ? (
                <div>

                </div>
            ) : (
                <p>leading </p>
            )
        }
        </>
    )
}

export default AllMovies
