import { useSelector, useDispatch } from "react-redux"
import { useEffect, useState } from "react"
import { useNavigate } from "react-router-dom"
import { Routes, Route, Link } from "react-router-dom"
import ManageUsers from "./users/ManageUsers"
import ManageMovies from "./movies/ManageMovies"

const Home = () => {

    const user = useSelector(state => state.user)
    const [clicked, setClicked] = useState('')
    const navigate = useNavigate()
    const dispatch = useDispatch()

    useEffect(() => {
        if (!user.fullName) {
            navigate('/')
        }
    }, [user, navigate])

    const handleClick = (e) => {
        setClicked(e.target.id)
    }

    const handleLogOut = (e) => {
        e.preventDefault()
        dispatch({type: 'RESET_USERS'})
        dispatch({type: 'LOG_OUT'})
        localStorage.removeItem('token')
        navigate('/')
    }

    
    return (
        <>
            <h1>Movies - subcsription Website</h1>
            <button style={{marginRight: '10px', backgroundColor: clicked === 'movies' && 'yellow'}} id="movies" onClick={(e) => handleClick(e)}>Movies</button>
            <button style={{marginRight: '10px', backgroundColor: clicked === 'subscriptions' && 'yellow'}} id="subscriptions" onClick={(e) => handleClick(e)}>Subscriptions</button>
            {user.isAdmin && <Link to='users'><button style={{marginRight: '10px', backgroundColor: clicked === 'users' && 'yellow'}} id="users" onClick={(e) => handleClick(e)}>User Management</button></Link>}
            <button onClick={ (e) => handleLogOut(e)}>Log Out</button>
            <br/>
            <Routes>
                <Route path="users/*" element={<ManageUsers />} />
                <Route path="movies/*" element={<ManageMovies />} />
            </Routes>
        </>
    )
}

export default Home
