import { useSelector } from "react-redux"
import { useEffect, useState } from "react"
import { useNavigate } from "react-router-dom"
import { Routes, Route, Link } from "react-router-dom"
import ManageUsers from "./users/ManageUsers"

const Home = () => {

    const user = useSelector(state => state.user)
    const [clicked, setClicked] = useState('')
    const navigate = useNavigate()

    useEffect(() => {
        if (!user.fullName) {
            navigate('/')
        }
    }, [user, navigate])

    const handleClick = (e) => {
        setClicked(e.target.id)
    }
    
    return (
        <>
            <h1>Movies - subcsription Website</h1>
            <button style={{marginRight: '10px', backgroundColor: clicked === 'subscriptions' && 'yellow'}} id="movies" onClick={(e) => handleClick(e)}>Movies</button>
            <button style={{marginRight: '10px', backgroundColor: clicked === 'subscriptions' && 'yellow'}} id="subscriptions" onClick={(e) => handleClick(e)}>Subscriptions</button>
            {user.isAdmin && <Link to='users'><button style={{marginRight: '10px'}} id="users" onClick={(e) => handleClick(e)}>User Management</button></Link>}
            <button>Log Out</button>
            <br/>
            <Routes>
                <Route path="users/*" element={<ManageUsers />} />
            </Routes>
        </>
    )
}

export default Home
