const initialState = []

const usersReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'SET_USERS':
            return {
                ...state,
                users: action.users
            }
        case 'UPDATE_USER':
            return {
                ...state,
                users: state.users.map(user => {
                    if (user._id === action.user._id) {
                        return action.user
                    } else {
                        return user
                    }
                })
            }
        case 'ADD_USER':
        default:
            return state
    }
}

export default usersReducer
