import {useSelector} from 'react-redux'

const Allusers = () => {

    const [users, setUsers] = useState([])
    setUsers = useSelector(state => state.systemUsers.users)

    return (
        <>
        {
            users.length && users.map((user) => {
                return (
                    <div key={user._id} style={{border: '1px solid black', width: 'fit-content', padding: '10px'}}>
                        <p>Name: {user.FirstName} {user.LastName}</p>
                        <p>User Name: {user.Username}</p>
                        <p>Session Time Out (minutes): {user.SessionTimeout.toString()}</p>
                        <p>Created Date: {user.CreatedDate.slice(0, 10)}</p>
                        <p>Permissions:</p>
                        <ul>
                        {
                            user.permissions.map((permission) => {
                                return (
                                    <li>{permission}</li>
                                )
                            })
                        }
                        </ul>
                    </div>
                )
            })
        }
        </>
    )
}

export default Allusers
