import { useParams } from "react-router"
import { useSelector } from "react-redux"

const EditMovie = () => {

    const {id} = useParams()
    const movies = useSelector(state => state.movies)
    const [movie, setMovie] = useState(movies.find(movie => movie._id === id))

    return (
        <>
            <h2>Edit Movie</h2>
            <p>{id}</p>
        </>
    )
}

export default EditMovie
