const initialState = []

const membersReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'SET_MEMBERS':
            return {
                ...state,
                members: action.members
            }
        case 'ADD_MEMBER':
            return {
                ...state,
                members: [...state.members, action.member]
            }
        case 'DELETE_MEMBER':
            return {
                ...state,
                members: state.members.filter(member => member._id !== action.id)
            }
        case 'RESET_MEMBERS':
            return {
                ...initialState
            }
