const initialState = {}

const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'SIGN_IN': {
            if (action.user.name === 'Ehab Adwan') {
            return {
                ...state,
                id: action.user.id,
                name: action.user.name,
                isAdmin: false
            }
        } else {
            return {
                ...state,
                id: action.user.id,
                name: action.user.name,
                isAdmin: false
            }
        }
        }
        default:
            return state
    }
}

export default userReducer
