const initialState = {}

const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'SIGN_IN': {
            return {
                ...state,
                user: action.user.id, 
                name
            }
        }
        default:
            return state
    }
}

export default userReducer
