import {useState} from 'react'
import axios from 'axios'
import { useDispatch } from 'react-redux'
const cinemaUrl = process.env.REACT_APP_CINEMAWS

const Login = () => {

    const [user, setUser] = useState({name: '', password: ''})
    const [message, setMessage] = useState('')

    const handleClick = async (e) => {
        e.preventDefault()
        try {
            const {data} = await axios.post(`${cinemaUrl}/users/login`, user)
            setMessage('')
            
        } catch (error) {
            console.log(error.response.data.user)
            setMessage(error.response.data.user.message)
        }
    }

    return (
        <>
            <h1>Login</h1>
            <form> 
                User Name: <input type="text" name="username" onChange={(e) => setUser({...user, name: e.target.value})}/>
                <br/>
                <br/>
                Password: <input type="text" name="password" onChange={(e) => setUser({...user, password: e.target.value})}/>
                <br/>
                <br/>
                <button onClick={(e) => handleClick(e)}>Login</button>
            </form>
            {
                message && <p>{message}</p>
            }
        </>
    )
}

export default Login
