import { useEffect, useState } from "react"
import { useSelector } from "react-redux"
import { Link, useParams } from "react-router-dom"

const EditMember = () => {

    const [member, setName] = useState({Name: '', Email: '', City: ''})
    const [message, setMessage] = useState('')
    const url = process.env.REACT_APP_SUBWS
    const members = useSelector(state => state.members)
    const {id} = useParams()

    useEffect(() => {
        setName(members.find(member => member._id === id))
    }, [])

    const handleClick = async () => {
        console.log(id)
        console.log(member)
    }

    return (
        <>
            <h2>Edit Member</h2>
            <div  style={{border: '1px solid black', padding: '10px'}}>
                <h3>Add new member</h3>
                <form>
                    <label>Name: <input type="text" name="name" defaultValue={members.Name} onChange={(e) => setName({...member, Name: e.target.value})} /></label><br/>
                    <label>Email: <input type="text" name="Email" defaultValue={members.Email} onChange={(e) => setName({...member, Email: e.target.value})} /></label><br/>
                    <label>City: <input type="text" name="City" defaultValue={members.City} onChange={(e) => setName({...member, City: e.target.value})} /></label><br/>
                </form>
                {message && <p>{message}</p>}
                <button style={{marginRight: '10px'}} onClick={() => handleClick()}>Save</button>
                <Link to="/home/subscriptions/allmembers"><button style={{marginRight: '10px'}}>Cancel</button></Link>
            </div>
        </>
    )
}

export default EditMember
