import { useSelector, useDispatch } from "react-redux"
import { useEffect, useState } from "react"
import { useNavigate } from "react-router-dom"
import { Routes, Route, Link } from "react-router-dom"
import ManageUsers from "./users/ManageUsers"
import ManageMovies from "./movies/ManageMovies"
import ManageSubs from "./subscriptions/ManageSubs"
import AddMovie from "./movies/AddMovie"
import EditMovie from ".mo/EditMovie"

const Home = () => {

    const user = useSelector(state => state.user)
    const [clicked, setClicked] = useState('')
    const navigate = useNavigate()
    const dispatch = useDispatch()

    useEffect(() => {
        if (!user.fullName) {
            navigate('/')
        }
    }, [user, navigate])

    const handleClick = (e) => {
        setClicked(e.target.id)
    }

    const handleLogOut = (e) => {
        e.preventDefault()
        dispatch({type: 'RESET_USERS'})
        dispatch({type: 'LOG_OUT'})
        dispatch({type: 'RESET_MOVIES'})
        localStorage.removeItem('token')
        navigate('/')
    }

    
    return (
        <>
            <h1>Movies - subcsription Website</h1>
            <Link to='movies'><button style={{marginRight: '10px', backgroundColor: clicked === 'movies' && 'yellow'}} id="movies" onClick={(e) => handleClick(e)}>Movies</button></Link>
            <Link to='subscriptions'><button style={{marginRight: '10px', backgroundColor: clicked === 'subscriptions' && 'yellow'}} id="subscriptions" onClick={(e) => handleClick(e)}>Subscriptions</button></Link>
            {user.isAdmin && <Link to='users'><button style={{marginRight: '10px', backgroundColor: clicked === 'users' && 'yellow'}} id="users" onClick={(e) => handleClick(e)}>User Management</button></Link>}
            <button onClick={ (e) => handleLogOut(e)}>Log Out</button>
            <br/>
            <Routes>
                <Route path="users/*" element={<ManageUsers />} />
                <Route path="movies/*" element={<ManageMovies />} />
                <Route path="subscriptions/*" element={<ManageSubs />} />
                <Route path="addmovie" element={<AddMovie />} />
                <Route path="editmovie/:id" element={<EditMovie />} />
            </Routes>
        </>
    )
}

export default Home
