import axios from "axios"
import { useEffect, useState, useCallback } from "react"
import { useDispatch } from "react-redux"
const ManageUsers = () => {

    const [users, setUsers] = useState([])
    const [clicked, setClicked] = useState('allUsers')
    const [message, setMessage] = useState('')

    const usersUrl = process.env.REACT_APP_CINEMAWS
    const dispatch = useDispatch()

    const getUsers = useCallback( async () => {
        try {
            const {data} = await axios.get(`${usersUrl}/users`);
            setUsers(data.users)
            dispatch({ type: 'SET_USERS', users: data.users})
        } catch (error) {
            setMessage(error.response.data)
        }
    }, [ dispatch, usersUrl ])

    useEffect(() => {
        getUsers()
    }, [getUsers])

    const handleClick = (e) => {
        setClicked(e.target.id)
    }

    return (
        <div style={{border: '1px solid black', width: '75%', margin: '20px auto'}}>
            <h1>Users</h1>
            <button style={{marginRight: '10px', backgroundColor: clicked === 'allUsers' && 'yellow'}} onClick={(e) => handleClick(e)} id="allUsers">All Users</button>
            <button style={{backgroundColor: clicked === 'addUser' && 'yellow'}} onClick={(e) => handleClick(e)} id="addUser">Add User</button>
            { message && <p>{message}</p> }
        </div>
    )
}

export default ManageUsers
