const initialState = {}

const moviesReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'SET_MOVIES':
            return {
                ...state,
                ...action.movies
            }
        case 'RESET_MOVIES':
            return {
                ...state            }
    default: 
        return {
            ...state
        }
    }
}

export default moviesReducer
