import {useSelector} from 'react-redux'
import axiosRequest from '../config/axiosConfig'
import {useEffect} from 'react'

const Subscribe = (props) => {

    const movies = useSelector(state => state.movies)
    const id = props.id
    const url = process.env.REACT_APP_SUBWS

    useEffect(() => {
        getMovieNames()
    }, [])

    const getMovieNames = async () => {
        const {data} = await axiosRequest.get(`${url}/movies/allmovies`)
        console.log(data)
    }

    return (
        <>
            <p>Add a new movie</p>
            <form>
                <select name="movie" id="movie">
                    {movies.map(movie => <option value={movie._id}>{movie.Title}</option>)}
                </select>
                <button>Subscribe</button>
            </form>
        </>
    )
}

export default Subscribe
