import axios from "axios"
import { useEffect, useState, useCallback } from "react"
import { useDispatch } from "react-redux"
import Allusers from "./allUsers"
import AddUser from "./addUser"
import {Routes, Route, Link, useNavigate} from "react-router-dom"
const ManageUsers = () => {

    //const [users, setUsers] = useState([])
    const [clicked, setClicked] = useState('allUsers')
    const [message, setMessage] = useState('')

    const usersUrl = process.env.REACT_APP_CINEMAWS
    const dispatch = useDispatch()
    const navigate = useNavigate()

    const getUsers = useCallback( async () => {
        try {
            const {data} = await axios.get(`${usersUrl}/users`);
            //setUsers(data.users)
            dispatch({ type: 'SET_USERS', users: data})
        } catch (error) {
            setMessage(error.response.data.message)
        }
    }, [ dispatch, usersUrl ])

    useEffect(() => {
        getUsers()
        navigate('allUsers')
    }, [getUsers])

    const handleClick = (e) => {
        setClicked(e.target.id)
    }

    return (
        <div style={{border: '1px solid black', width: '75%', margin: '20px auto', textAlign: 'left', padding: '10px'}}>
            <h1>Users</h1>
            <Link to="allUsers"><button style={{marginRight: '10px', backgroundColor: clicked === 'allUsers' && 'yellow'}} onClick={(e) => handleClick(e)} id="allUsers">All Users</button></Link>
            <Link><button style={{backgroundColor: clicked === 'addUser' && 'yellow'}} onClick={(e) => handleClick(e)} id="addUser">Add User</button></Link>
            { message && <p>{message}</p> }
            {
                <Routes>
                    <Route path="allUsers" element={<Allusers />} />
                    <Route path="addUsers" element={<AddUser />} />
                </Routes>
            }
        </div>
    )
}

export default ManageUsers
