import { useSelector } from "react-redux"
import { useEffect } from "react"
import { useNavigate } from "react-router-dom"

const Home = () => {

    const user = useSelector(state => state.user)
    const navigate = useNavigate()

    useEffect(() => {
        if (!user.name) {
            navigate('/')
        }
    }, [user, navigate])
    return (
        <>
            <h1>Home Page</h1>
            <button style={{marginRight: '10px'}}>Movies</button>
            <button style={{marginRight: '10px'}}>Subscriptions</button>
            {user.isAdmin && <button style={{marginRight: '10px'}}>User Management</button>}
            <button>Log Out</button>
            
        </>
    )
}

export default Home
